.then (response => {
        dispatch({type: PAGAR_PEDIDO, payload: response.data});
    cb(null);
    Router.push('/sucesso');
    dispatch(cleanCarrinho());        
})
.catch(e => cb (errorHandling(e)));
}
*/

export const pagarPedido = (id, token, senderHash, cb = null) => dispatch => {
        (getHeaders(token)
)
.then (response => {
    dispatch({type: PAGAR_PEDIDO, payload: response.data});
    cb(null);
    Router.push('/sucesso');
    dispatch(cleanCarrinho());        
})
.catch(e => cb (errorHandling(e)));
}


export default {
    setForm,
    cleanForm,
    setTipoPagamento,
    getSessionPagamento,
    novoPedido,
    pagarPedido
};
